{
  "name": "fantasy-soccer-backend",
  "version": "1.0.0",
  "description": "Backend for the interactive fantasy soccer dashboard",
  "main": "dist/index.js",
  "scripts": {
    "start": "node dist/index.js",
    "dev": "nodemon src/index.ts",
    "build": "tsc",
