export interface Player {
  id: string;
  name: string;
  club: string;
  position: 'GK' | 'DEF' | 'MID' | 'FWD';
  photo: string;
}

export interface PlayerStats {
  goals: number;
  assists: number;
  appearances: number;
  cleanSheets: number;
  yellowCards: number;
  redCards: number;
}

export interface PlayerDetail extends Player {
  stats: PlayerStats;
  fantasyScore: number;
}

import React from 'react';
import { Pie } from 'react-chartjs-2';
import { Chart as ChartJS, ArcElement, Tooltip, Legend } from 'chart.js';
import { PlayerDetail } from '../types/player';

ChartJS.register(ArcElement, Tooltip, Legend);

interface FantasyTeamPositionChartProps {
  players: PlayerDetail[];
}

const FantasyTeamPositionChart: React.FC<FantasyTeamPositionChartProps> = ({ players }) => {
  const positionCounts = players.reduce((acc, player) => {
    acc[player.position] = (acc[player.position] || 0) + 1;
    return acc;
  }, {} as Record<string, number>);

  const data = {
    labels: Object.keys(positionCounts),
    datasets: [
      {
        label: 'Players',
        data: Object.values(positionCounts),
        backgroundColor: [
          'rgba(255, 99, 132, 0.6)', // FWD
          'rgba(54, 162, 235, 0.6)', // MID
          'rgba(255, 206, 86, 0.6)', // DEF
          'rgba(75, 192, 192, 0.6)', // GK
        ],
        borderColor: [
          'rgba(255, 99, 132, 1)',
          'rgba(54, 162, 235, 1)',
          'rgba(255, 206, 86, 1)',
          'rgba(75, 192, 192, 1)',
        ],
        borderWidth: 1,
      },
    ],
  };

  const options = {
    responsive: true,
    plugins: {
      legend: {
        position: 'top' as const,
      },
      title: {
        display: true,
        text: 'Fantasy Team Position Distribution',
      },
    },
  };

  return (
    <div className="w-full md:w-1/2 lg:w-1/3 mx-auto p-4">
      <Pie data={data} options={options} />
    </div>
  );
};

export default FantasyTeamPositionChart;

import React, { useState, useEffect } from 'react';
import { PlayerDetail, PlayerStats } from '../types/player';

interface PlayerCardProps {
  player: PlayerDetail;
  onAddToTeam: (player: PlayerDetail) => void; // New prop for adding to team
}

const PlayerCard: React.FC<PlayerCardProps> = ({ player, onAddToTeam }) => {
  // Note: Stats and fantasyScore are now directly available in player: PlayerDetail
  // No need to fetch them individually here anymore

  return (
    <div className="border p-4 m-2 rounded shadow-lg flex flex-col items-center bg-white">
      <img src={player.photo} alt={player.name} className="w-24 h-24 rounded-full mx-auto mb-2 object-cover" />
      <h3 className="font-bold text-lg text-center">{player.name}</h3>
      <p className="text-center text-gray-600">{player.club} - {player.position}</p>

      <div className="mt-4 text-sm w-full text-left">
        <p><strong>Goals:</strong> {player.stats.goals}</p>
        <p><strong>Assists:</strong> {player.stats.assists}</p>
        <p><strong>Appearances:</strong> {player.stats.appearances}</p>
        <p><strong>Clean Sheets:</strong> {player.stats.cleanSheets}</p>
        <p><strong>Yellow Cards:</strong> {player.stats.yellowCards}</p>
        <p><strong>Red Cards:</strong> {player.stats.redCards}</p>
        <p className="text-lg font-bold mt-2">Fantasy Score: {player.fantasyScore}</p>
      </div>
      <button
        onClick={() => onAddToTeam(player)}
        className="mt-4 bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
      >
        Add to Team
      </button>
    </div>
  );
};

export default PlayerCard;

import React from 'react';
import { Bar } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend } from 'chart.js';
import { PlayerDetail } from '../types/player';

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

interface FantasyScoreBarChartProps {
  players: PlayerDetail[];
}

const FantasyScoreBarChart: React.FC<FantasyScoreBarChartProps> = ({ players }) => {
  const data = {
    labels: players.map(player => player.name),
    datasets: [
      {
        label: 'Fantasy Score',
        data: players.map(player => player.fantasyScore),
        backgroundColor: 'rgba(153, 102, 255, 0.6)',
        borderColor: 'rgba(153, 102, 255, 1)',
        borderWidth: 1,
      },
    ],
  };

  const options = {
    responsive: true,
    plugins: {
      legend: {
        position: 'top' as const,
      },
      title: {
        display: true,
        text: 'Fantasy Team Score Comparison',
      },
    },
    scales: {
      y: {
        beginAtZero: true,
        title: {
          display: true,
          text: 'Fantasy Score',
        },
      },
    },
  };

  return (
    <div className="w-full md:w-full lg:w-2/3 mx-auto p-4">
      <Bar data={data} options={options} />
    </div>
  );
};

export default FantasyScoreBarChart;
